	info7 := core.EnsureComponentRegistered[T7](componentsRegistry)

	// 2. Add to Blueprint (Build Mask)

	mustAdd(info1)

	mustAdd(info2)

	mustAdd(info3)

	mustAdd(info4)

	mustAdd(info5)

	mustAdd(info6)

	mustAdd(info7)

