// All returns an iterator (iter.Seq2) that yields the unique Entity identifier
// and a tuple of pointers to its 7 components. This is designed for use
// in range loops, providing a clean, idiomatic Go way to process data while
// maintaining maximum cache efficiency.
//
// The iteration is performed archetype by archetype, ensuring that data is
// accessed contiguously in memory, which significantly reduces CPU cache misses.
//
// Example usage:
//
//	for head, tail := range view7.All() {
//	    entity := head.Entity
//	    v1 := head.V1
//
//	    v7 := tail.V7
//
//	}
func (v *View7[T1, T2, T3, T4, T5, T6, T7]) All() iter.Seq2[
	struct {
		Entity core.Entity
		V1     *T1
		V2     *T2
		V3     *T3
	},
	struct {
		V4 *T4
		V5 *T5
		V6 *T6
		V7 *T7
	},
] {
	return func(yield func(
		struct {
			Entity core.Entity
			V1     *T1
			V2     *T2
			V3     *T3
		},
		struct {
			V4 *T4
			V5 *T5
			V6 *T6
			V7 *T7
		},
	) bool) {
		const strideEntity = unsafe.Sizeof(core.Entity(0))
		s1 := unsafe.Sizeof(*new(T1))
		s2 := unsafe.Sizeof(*new(T2))
		s3 := unsafe.Sizeof(*new(T3))
		s4 := unsafe.Sizeof(*new(T4))
		s5 := unsafe.Sizeof(*new(T5))
		s6 := unsafe.Sizeof(*new(T6))
		s7 := unsafe.Sizeof(*new(T7))

		for _, b := range v.Baked {

			// 1. Setup Entity Pointer
			pEntity := b.GetEntityColumn().Data

			// 2. Setup Component Pointers
			p1 := b.GetColumn(0).Data
			p2 := b.GetColumn(1).Data
			p3 := b.GetColumn(2).Data
			p4 := b.GetColumn(3).Data
			p5 := b.GetColumn(4).Data
			p6 := b.GetColumn(5).Data
			p7 := b.GetColumn(6).Data

			n := b.Arch.Len()
			// Death Loop (Pointer Arithmetic)
			for n > 0 {

				// Construct Head
				head := struct {
					Entity core.Entity
					V1     *T1
					V2     *T2
					V3     *T3
				}{
					Entity: *(*core.Entity)(pEntity), V1: (*T1)(p1), V2: (*T2)(p2), V3: (*T3)(p3),
				}

				// Construct Tail

				tail := struct {
					V4 *T4
					V5 *T5
					V6 *T6
					V7 *T7
				}{V4: (*T4)(p4), V5: (*T5)(p5), V6: (*T6)(p6), V7: (*T7)(p7)}

				if !yield(head, tail) {
					return
				}

				// Increment Pointers
				pEntity = unsafe.Add(pEntity, strideEntity)
				p1 = unsafe.Add(p1, s1)
				p2 = unsafe.Add(p2, s2)
				p3 = unsafe.Add(p3, s3)
				p4 = unsafe.Add(p4, s4)
				p5 = unsafe.Add(p5, s5)
				p6 = unsafe.Add(p6, s6)
				p7 = unsafe.Add(p7, s7)

				n--
			}
		}
	}
}

// Filter returns an iterator (iter.Seq2) that yields only the entities
// specified in the selected slice, provided they match the View's archetype
// constraints. It is optimized for scenarios where a subset of entities
// is pre-determined (e.g., via spatial partitioning or sorted results)
// but requires high-speed access to their component data.
//
// The iterator performs an internal validation for each entity to ensure
// it still belongs to an archetype compatible with this View, preventing
// invalid memory access if the entity's composition has changed.
//
// Example usage:
//
//	selected := []Entity{e1, e5, e10}
//	for head, tail := range view7.Filter(selected) {
//	    entity := head.Entity
//	    v1 := head.V1
//
//
//	    v7 := tail.V7
//
//
//	}
func (v *View7[T1, T2, T3, T4, T5, T6, T7]) Filter(selected []Entity) iter.Seq2[
	struct {
		Entity Entity
		V1     *T1
		V2     *T2
		V3     *T3
	},
	struct {
		V4 *T4
		V5 *T5
		V6 *T6
		V7 *T7
	},
] {
	return func(yield func(
		struct {
			Entity Entity
			V1     *T1
			V2     *T2
			V3     *T3
		},
		struct {
			V4 *T4
			V5 *T5
			V6 *T6
			V7 *T7
		},
	) bool) {
		stride1 := unsafe.Sizeof(*new(T1))
		stride2 := unsafe.Sizeof(*new(T2))
		stride3 := unsafe.Sizeof(*new(T3))
		stride4 := unsafe.Sizeof(*new(T4))
		stride5 := unsafe.Sizeof(*new(T5))
		stride6 := unsafe.Sizeof(*new(T6))
		stride7 := unsafe.Sizeof(*new(T7))

		var ptr1, ptr2, ptr3, ptr4, ptr5, ptr6, ptr7 unsafe.Pointer
		var lastArchID int32 = -1
		registry := v.Reg.ArchetypeRegistry
		col1ID := v.Layout[0].ID
		col2ID := v.Layout[1].ID
		col3ID := v.Layout[2].ID
		col4ID := v.Layout[3].ID
		col5ID := v.Layout[4].ID
		col6ID := v.Layout[5].ID
		col7ID := v.Layout[6].ID

		for _, e := range selected {
			link, ok := registry.EntityLinkStore.Get(e)
			if !ok {
				continue
			}

			if int32(link.ArchId) != lastArchID {
				arch := &registry.Archetypes[link.ArchId]
				if !v.View.Matches(arch.Mask) {
					lastArchID = -1
					continue
				}

				ptr1 = arch.GetColumn(col1ID).Data
				ptr2 = arch.GetColumn(col2ID).Data
				ptr3 = arch.GetColumn(col3ID).Data
				ptr4 = arch.GetColumn(col4ID).Data
				ptr5 = arch.GetColumn(col5ID).Data
				ptr6 = arch.GetColumn(col6ID).Data
				ptr7 = arch.GetColumn(col7ID).Data

				lastArchID = int32(link.ArchId)
			}

			row := uintptr(link.Row)

			head := struct {
				Entity Entity
				V1     *T1
				V2     *T2
				V3     *T3
			}{
				Entity: e, V1: (*T1)(unsafe.Add(ptr1, row*stride1)), V2: (*T2)(unsafe.Add(ptr2, row*stride2)), V3: (*T3)(unsafe.Add(ptr3, row*stride3)),
			}

			tail := struct {
				V4 *T4
				V5 *T5
				V6 *T6
				V7 *T7
			}{V4: (*T4)(unsafe.Add(ptr4, row*stride4)), V5: (*T5)(unsafe.Add(ptr5, row*stride5)), V6: (*T6)(unsafe.Add(ptr6, row*stride6)), V7: (*T7)(unsafe.Add(ptr7, row*stride7))}

			if !yield(head, tail) {
				return
			}
		}
	}
}

